import ipfs from '../ipfs';
import React, { Component } from 'react'

const accepts = [
    { name: "images", acc: "image/*", },
    { name: "audio", acc: "audio/*", },
    { name: "pdf", acc: "application/pdf,application/msword," }

]




export class index extends Component {
    componentWillMount() {
        this.setState({
            file: null,
            ipfsHash: null,
            format: "null"
        })
    }


    captureChange = (event) => {
        console.log("caputreFile")
        event.preventDefault()
        const file = event.target.files[0]
        const reader = new window.FileReader()
        reader.readAsArrayBuffer(file)
        reader.onloadend = () => {
            this.setState({ file: Buffer(reader.result) })
            console.log('buffer', this.state.file)
        }
    }

    submit = (event) => {


        event.preventDefault()
        console.log("onSubmit")
        ipfs.files.add(this.state.file, async (err, res) => {
            if (err) {
                console.error(err)
                return
            }
            this.setState({ ipfsHash: res[0].hash })
            console.log(res)
            console.log('ipfsHash', this.state.ipfsHash)


        })
    }
    format = (event) => {
        event.preventDefault()
        console.log("onChange")

        setState({ format: "res[0].hash " })
        console.log('ipfsHash', this.state.format)




    }
    render() {
        return (
            <div>
                <select onChange={this.format}>
                    <option>Choose....</option>
                    {
                        accepts.map(a =>
                            <option>{a.name}</option>
                        )
                    }
                </select>
                <input type="file" multiple onChange={this.captureChange} accept="" />
                <button onClick={this.submit}>Submit</button>
                {this.state.ipfsHash ? this.state.ipfsHash : "Upload some file"}
                <img src={`https://ipfs.io/ipfs/${this.state.ipfsHash}`} />


            </div>
        )
    }
}

export default index
